interface paths {
    /**
     * Basedir
     */
    basedir: string;
    /**
     * Basedir hash
     */
    hash: string;
    /**
     * Input directory
     * @default src
     */
    input: string;
    /**
     * Modules directory
     * @default node_modules
     */
    modules: string;
    /**
     * Output directory
     * @default dist
     */
    output: string;
    /**
     * Directory for temporary files
     * @default os-cache
     */
    storage: string;
}
/**
 * Cache paths
 */
declare const paths: paths;
declare const get: (basedir: string) => paths;
export { get, paths };
