import BudCommand from '@roots/bud/cli/commands';
import { Command, Option } from '@roots/bud-support/clipanion';
/**
 * Bud stylelint command
 */
export class BudStylelintCommand extends BudCommand {
    /**
     * {@link BudCommand.paths}
     */
    static paths = [
        [`lint`, `css`],
        [`lint`, `scss`],
        [`lint`, `sass`],
        [`stylelint`],
    ];
    /**
     * {@link BudCommand.usage}
     */
    static usage = Command.Usage({
        category: `tool`,
        description: `Run stylelint on source files`,
        examples: [[`Run stylelint on source files`, `$0 lint css`]],
    });
    /**
     * Command options
     */
    options = Option.Proxy({ name: `stylelint passthrough options` });
    /**
     * Execute command
     */
    async execute() {
        await this.makeBud();
        await this.bud.run();
        return await this.run([`stylelint`, `bin`, `stylelint.mjs`], this.options, [this.bud.path(`@src`, `**`, `*.{css,sass,scss}`)]);
    }
}
